import { useState } from "react";

const ContactAdder = () => {
  const [name, setName] = useState("rajan");
  const onClickHandler = () => {
    setName("Mahesh");
  };
  return (
    <>
      Contact Adder:
      <p>
        Hello {name}.
        {name === "Mahesh" ? "I live in Japan." : "I am not Mahesh"}
      </p>
      <button onClick={onClickHandler}>Click me</button>
    </>
  );
};

export default ContactAdder;
